                283 => new Code(code, "RPC_E_ACCESS_DENIED", "Access is denied."),
                284 => new Code(code, "RPC_E_REMOTE_DISABLED", "Remote calls are not allowed for this process."),
                285 => new Code(code, "RPC_E_INVALID_OBJREF", "The marshaled interface data packet (OBJREF) has an invalid or unknown format."),
                286 => new Code(code, "RPC_E_NO_CONTEXT", "No context is associated with this call. This happens for some custom marshaled calls and on the client side of the call."),
                287 => new Code(code, "RPC_E_TIMEOUT", "This operation returned because the time-out period expired."),
                288 => new Code(code, "RPC_E_NO_SYNC", "There are no synchronize objects to wait on."),
                289 => new Code(code, "RPC_E_FULLSIC_REQUIRED", "Full subject issuer chain Secure Sockets Layer (SSL) principal name expected from the server."),
                290 => new Code(code, "RPC_E_INVALID_STD_NAME", "Principal name is not a valid Microsoft standard (msstd) name."),
                291 => new Code(code, "CO_E_FAILEDTOIMPERSONATE", "Unable to impersonate DCOM client."),
                292 => new Code(code, "CO_E_FAILEDTOGETSECCTX", "Unable to obtain server's security context."),
                293 => new Code(code, "CO_E_FAILEDTOOPENTHREADTOKEN", "Unable to open the access token of the current thread."),
                294 => new Code(code, "CO_E_FAILEDTOGETTOKENINFO", "Unable to obtain user information from an access token."),
                295 => new Code(code, "CO_E_TRUSTEEDOESNTMATCHCLIENT", "The client who called IAccessControl::IsAccessPermitted was not the trustee provided to the method."),
