                301 => new Code(code, "CO_E_INVALIDSID", "One of the security identifiers provided by the user was invalid."),
                302 => new Code(code, "CO_E_CONVERSIONFAILED", "Unable to convert a wide character trustee string to a multiple-byte trustee string."),
                303 => new Code(code, "CO_E_NOMATCHINGSIDFOUND", "Unable to find a security identifier that corresponds to a trustee string provided by the user."),
                304 => new Code(code, "CO_E_LOOKUPACCSIDFAILED", "The system function LookupAccountSID failed."),
                305 => new Code(code, "CO_E_NOMATCHINGNAMEFOUND", "Unable to find a trustee name that corresponds to a security identifier provided by the user."),
                306 => new Code(code, "CO_E_LOOKUPACCNAMEFAILED", "The system function LookupAccountName failed."),
                307 => new Code(code, "CO_E_SETSERLHNDLFAILED", "Unable to set or reset a serialization handle."),
                308 => new Code(code, "CO_E_FAILEDTOGETWINDIR", "Unable to obtain the Windows directory."),
                309 => new Code(code, "CO_E_PATHTOOLONG", "Path too long."),
                310 => new Code(code, "CO_E_FAILEDTOGENUUID", "Unable to generate a UUID."),
                311 => new Code(code, "CO_E_FAILEDTOCREATEFILE", "Unable to create file."),
                312 => new Code(code, "CO_E_FAILEDTOCLOSEHANDLE", "Unable to close a serialization handle or a file handle."),
                313 => new Code(code, "CO_E_EXCEEDSYSACLLIMIT", "The number of access control entries (ACEs) in an ACL exceeds the system limit."),
                314 => new Code(code, "CO_E_ACESINWRONGORDER", "Not all the DENY_ACCESS ACEs are arranged in front of the GRANT_ACCESS ACEs in the stream."),
                315 => new Code(code, "CO_E_INCOMPATIBLESTREAMVERSION", "The version of ACL format in the stream is not supported by this implementation of IAccessControl."),
                316 => new Code(code, "CO_E_FAILEDTOOPENPROCESSTOKEN", "Unable to open the access token of the server process."),
                317 => new Code(code, "CO_E_DECODEFAILED", "Unable to decode the ACL in the stream provided by the user."),
                319 => new Code(code, "CO_E_ACNOTINITIALIZED", "The COM IAccessControl object is not initialized."),
                320 => new Code(code, "CO_E_CANCEL_DISABLED", "Call Cancellation is disabled."),
                65535 => new Code(code, "RPC_E_UNEXPECTED", "An internal error occurred."),
                _ => Unknown(code),
            };
        }
    }
}
