        }

        internal override Code Resolve(bool failure, int code)
        {
            if (!failure)
            {
                return code switch
                {
                    // https://learn.microsoft.com/en-us/windows/deployment/update/windows-update-error-reference#windows-update-success-codes
                    0x0001 => new Code(code, "WU_S_SERVICE_STOP", "Windows Update Agent was stopped successfully."),
                    0x0002 => new Code(code, "WU_S_SELFUPDATE", "Windows Update Agent updated itself."),
                    0x0003 => new Code(code, "WU_S_UPDATE_ERROR", "Operation completed successfully but there were errors applying the updates."),
                    0x0004 => new Code(code, "WU_S_MARKED_FOR_DISCONNECT", "A callback was marked to be disconnected later because the request to disconnect the operation came while a callback was executing."),
                    0x0005 => new Code(code, "WU_S_REBOOT_REQUIRED", "The system must be restarted to complete installation of the update."),
                    0x0006 => new Code(code, "WU_S_ALREADY_INSTALLED", "The update to be installed is already installed on the system."),
                    0x0007 => new Code(code, "WU_S_ALREADY_UNINSTALLED", "The update to be removed isn't installed on the system."),
                    0x0008 => new Code(code, "WU_S_ALREADY_DOWNLOADED", "The update to be downloaded has already been downloaded."),
                    _ => base.Resolve(failure, code)
                };
            }

            return code switch
            {
                // https://learn.microsoft.com/en-us/windows/deployment/update/windows-update-error-reference#automatic-update-errors
                0xA000 => new Code(code, "WU_E_AU_NOSERVICE", "Automatic Updates was unable to service incoming requests."),
